import React, {useState, useEffect} from 'react';
import { AppBar, Toolbar, Typography, Button, Avatar} from '@material-ui/core';
import {Link, useHistory, useLocation} from 'react-router-dom';
//import {useDispatch} from 'react-redux';
import useStyles from './styles';
import ggsipu from '../../images/ggsipu.png';

const Navbar = () => {
    const classes = useStyles();
    //const [user, setUser] = useState(JSON.parse(localStorage.getItem('profile')));
    //const dispatch = useDispatch();
    //const history = useHistory();
    //const location = useLocation();

    const user = null;
    //const logout = () => {
    //    dispatch({ type: 'LOGOUT'});
    //    history.push('/');
    //    setUser(null);
    //};

    //useEffect(() => {
    //    const token = user?.token;
    //    setUser(JSON.parse(localStorage.getItem('profile')));
    //}, [location]);

    return (
        <AppBar className={classes.appBar} position="static" color="inherit">
            <div className={classes.brandContainer}>
                <img className={classes.image} src={ggsipu} alt="ggsipu" height="50" />
                <Typography component={Link} to="/"  className={classes.heading} variant="h5" align="center">SOCIETIES</Typography>
            </div>
            <Toolbar className={classes.toolbar}>
                {user ? (
                    <div className={classes.profile}>
                        <Avatar className={classes.purple} alt={user.result.name} src={user.result.imageUrl}>{user.result.name.charAt(0)}</Avatar>
                        <Typography className={classes.userName} variant="h6">{user.result.name}</Typography>
                        <button variant="contained" className={classes.logout} color="secondary" /*onClick={logout}*/>Logout!</button>
                    </div>
                ) : ( 
                    <Button component={Link} to="/auth" variant="contained" className={classes.logout} color="primary">Sign In</Button>
                )}

            </Toolbar> 
        </AppBar>
    )
};

export default Navbar;
