import React, {useState, useRef} from 'react';
import {Typograghy, TextField, Button} from '@material-ui/core';
import {useDispatch} from 'react-redux';

import useStyles from './styles';
import {commentPost} from '../../actions/posts';

const CommentSection = ({post}) => {
    const classes = useStyles();
    const [ comments, setComments ] = useState(post?.comments);
    const [ comment, setComment] = useState('');
    const user = JSON.parse(localStorage.getItem('profile'));
    const dispatch = useDispatch();
    const commentsRef = useRef();

    const handleClick = async () => {
        const finalComment = `${user.result.name}: ${comment}`;
        const newComments = dispatch(commentPost(finalComment, post._id));
        setComments(newComments);
        setComment('');
        commentsRef.current.scrollIntoView({behavior: 'smooth'});
    };

    return(
        <div>
            <div className={classes.commentsOuterContainer}>
                <div className={classes.commentsInnerContainer}>
                    <Typograghy gutterBottom variant="h6">Comments</Typograghy>
                    {comments.map((c ,i) => (
                        <Typograghy key={i} gutterBottom variant="subtitle1">
                            <strong>{c.split(': ')[0]}</strong>
                            {c.split(': ')[1]}
                        </Typograghy>
                    ))}
                    <div ref={commentsRef}/>
                </div>
                {user?.result?.name && (
                <div style={{width: '70%'}}>
                    <Typograghy gutterBottom variant="h6">Write a Comment</Typograghy>
                    <TextField fullWidth rows={4} variant="outlined" label="Comment" multiline value={comment} onChange={(e) => setComment(e.target.value)} />
                    <Button style={{marginTop: '10px'}} fullWidth disabled={!comment} variant="contained" color="primary" onClick={handleClick}>
                        Comment
                    </Button>
                </div>
                )}
            </div>
        </div>
    );
};

export default CommentSection;
